mapper.select:
    select * from vpn.videoitems_1 
    where 1=1 
	    
	        and channelType=#{channelType}
	     
	    
	        and baseurl=#{baseurl}
	     
	    
	        and channel=#{channel}
	     
	    
	        and LOWER(name) like CONCAT('%',#{keyword},'%')
	    
    order by sortType desc,id desc

    select * from vpn.video_user_item_1
    
   		 
	        channel=#{channel}
	    
	     
	        and userId=#{userId}
	    
	    
	    
	        and LOWER(name) like CONCAT('%',#{keyword},'%')
	    
    
   
    order by id desc

select * from vpn.video_tv_channel where enable=1 

    and channel=#{channel}
 

    and LOWER(name) like CONCAT('%',#{keyword},'%')
